sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.choices = [];
	var gr = new GlideRecord('x_165574_slackoff_slackoff_job');
	gr.orderBy('type');
	gr.orderBy('output');
	gr.query();
	while(gr.next()){
		var option = {
			'type': gr.type.getDisplayValue().toString(),
			'output': gr.output.toString()
		};
		data.choices.push(option);
	}
})();
